  import Plot from 'svelte-plotly.js'

  const words = word_string.split(' ')
  const wordFreq = {}

  for (const word of words) {
    if (wordFreq[word]) {
      wordFreq[word]++
    } else {
      wordFreq[word] = 1
    }
  }

  const sortedWordFreq = Object.entries(wordFreq).sort((a, b) => b[1] - a[1])
  const topWords = []
  const topFrequencies = []

  const numWordsToExtract = 15
  const wordsExtracted = Math.min(numWordsToExtract, sortedWordFreq.length)

  for (let i = 0; i < wordsExtracted; i++) {
    const [word, frequency] = sortedWordFreq[i]
    topWords.push(word)
    topFrequencies.push(frequency)
  }

  const data = [
    {
      x: topWords,
      y: topFrequencies,
      type: 'bar'
    }
  ]

  const layout = {
    title: 'Frequency of Top 15 Words'
  }
